mapper.select:
SELECT o.*, p.title as product_title, p.cover_image, buyer.nickname as buyer_nickname, seller.nickname as seller_nickname, ml.name as location_name,
       ua.recipient_name, ua.phone, CONCAT(ua.province, ' ', ua.city, ' ', ua.district, ' ', ua.detailed_address) as full_address

WHERE o.id = #{orderId}

SELECT o.*, p.title as product_title, p.cover_image, buyer.nickname as buyer_nickname, seller.nickname as seller_nickname, ml.name as location_name,
       ua.recipient_name, ua.phone, CONCAT(ua.province, ' ', ua.city, ' ', ua.district, ' ', ua.detailed_address) as full_address



        o.id = #{orderId}


        AND o.delivery_method = #{deliveryMethod}


ORDER BY o.create_time DESC

mapper.update:
UPDATE orders
order_status = #{orderStatus}, total_price = #{totalPrice}, delivery_method = #{deliveryMethod}, meetup_location_id = #{meetupLocationId}, meetup_time_slot = #{meetupTimeSlot}, shipping_provider = #{shippingProvider}, tracking_number = #{trackingNumber},
WHERE id = #{id}
