mapper.select:
SELECT 
FROM product p JOIN user u ON p.seller_id = u.id
WHERE p.status = 'AVAILABLE'

    AND p.title LIKE CONCAT('%', #{keyword}, '%')


    AND p.category_id = #{categoryId}


    AND p.price >= #{minPrice}


    AND p.price <= #{maxPrice}



        ORDER BY p.price ASC, p.create_time DESC


        ORDER BY p.price DESC, p.create_time DESC


        ORDER BY p.create_time DESC

SELECT 
FROM product p JOIN user u ON p.seller_id = u.id
WHERE p.id = #{productId}

mapper.update:
UPDATE product SET category_id = #{categoryId}, title = #{title}, description = #{description}, price = #{price}, condition_level = #{conditionLevel},
cover_image = #{coverImage},
update_time = NOW() WHERE id = #{id}
