mapper.select:
SELECT * FROM user


        (username LIKE CONCAT('%', #{keyword}, '%') OR nickname LIKE CONCAT('%', #{keyword}, '%'))


        AND role = #{role}


        AND status = #{status}


ORDER BY create_time DESC

mapper.update:
UPDATE user
nickname = #{nickname}, password = #{password}, role = #{role}, status = #{status}, credit_score = #{creditScore},
WHERE id = #{id}
